  /* Define a animação de flutuação */
@keyframes floatAndZoomAnimation {
  0%, 100% {
    transform: translateY(0) scale(1); /* Posição e tamanho original */
  }
  50% {
    transform: translateY(-15px) scale(1.03); /* Flutua para cima e aumenta um pouco (3%) */
  }
}

/* Estilo base para a imagem dentro do banner */
.banner picture img {
  cursor: pointer;
  transition: transform 0.4s ease-in-out; /* Aumentar a transição para suavizar o zoom e a flutuação */
  display: block; /* Garante que as transformações funcionem corretamente */
  max-width: 100%; /* Boa prática para imagens responsivas */
  height: auto;
}

/* Aplica a animação de flutuação e zoom quando a classe 'floating' está presente */
.banner picture img.floating {
  animation: floatAndZoomAnimation 2s ease-in-out infinite;
  /* Você pode adicionar um zoom inicial ao ativar a classe, se desejar */
  /* transform: scale(1.03); */
}

/* Opcional: Efeito hover simples para mostrar que é clicável */
.banner picture img:hover {
  transform: scale(1.01); /* Um pequeno zoom ao passar o mouse */
}
