css:
/*pc*/@media (min-width: 601px){
  .header{box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);}
.header__logo { margin-left: -30px;max-height: 120px; max-width: 300px;}
#search-input {border-bottom: 1px solid #003765;width: 200px;margin-left: 590px}
.header__search-input {border-radius: 0px;font-size: 0;} 
  .header__search {width: 20px;margin-left: 50px;}
.header__nav {font-size: 16px;margin-top: -64px;width: 50%;margin-left: 290px;}
.header__nav a {color:#003765;border-bottom: 2px solid transparent;} 
  .header__nav a:hover { border-bottom-color: transparent;}
 .header__nav a:hover { border-bottom-color: 2px solid transparent;
   color:#00882e;font-weight: 600;}
  .header__category:hover {background:transparent;text-decoration: none;}
  a:hover {color: #003765;text-decoration: none;}
  a {color: #000;}
  .top-bar__page-links a { font-size:14px;font-weight: 500;}
  .top-bar__nav a { color: #fff;}
  .top-bar {background: #00882e;width: 100%;}
  h4{color:#003765;}
  h5{font-size: 15px;}
  h1 {font-size: 26px;color:#003765;font-weight: 600;}
  .prices--big .prices__value {font-size: 18px;color:#00882e;}
.product-card__name{font-size: 16px;padding-top: 10px;}
 .form-control-lg {
    border-radius: 20px;
    font-size: 16px;
    height: calc(1.5em + 1rem + 2px);
    line-height: 1.5;
    padding: 0.5rem 1rem;
} 
}
 /*mobile*/@media (max-width: 600px) {
   .top-bar{{display:none;}

}

 /*responsivo*/

.btn-primary {background-color: #de192c;border-color: #b70819;
    color:#fff;border-radius: 20px;}
.btn-primary:hover {background-color: #00882e;border-color: #00882e;
    color:#fff;border-radius: 20px;}
   .btn-lg {
     border-radius: 20px}
   .btn-light:hover {background-color: #00882e;border-color:#00882e;
    border-radius:20px;color: #ffffff;}
   .btn-light {border-radius:20px;background-color: #a1cbaf;border-color:#a1cbaf;color: #003765;}
  .btn-light:disabled {border-radius:20px;background-color: #f6f6f6;
   border-color: #f6f6f6;color: #a8a8a9;}
   .card-header {background-color: #EF7115;}
   .close {color: #fff;font-size: 26px;}
   .prices__value {color: #003765;}
   .glide__arrow {color: #00882e;}
   .header__buttons .btn{color: #003765;font-size:30px;}
   .header__buttons .btn:hover {color: #00882e;font-size:30px;}
   .badge {border-radius: 20px;}
   .badge-primary {background-color: #00882e;}
   .header__buttons .btn .badge {font-size: 14px;}
   .info-bar__item {font-size: 16px;}
   .header__search-btn{color: #ef7115;}
   .i-user{color:#003765;font-size: 30px;}
   .i-shopping-bag{color:#00882e;font-size: 30px;}
   .cart-count{background-color: #ef7115;border-radius:10px;
    color: var(--primary-yiq);}
   .info-bar {background: #003765;}
  .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
  .btn-primary {background-color: #de192c;border-radius: 3px;font-size: 14px;
     font-weight: 500;border-color: #de192c;}
  .btn-primary a:hover {background-color: #00882e;border-radius: 3px;font-size: 14px;
     font-weight: 500;border-color: #00882e;}
  .footer {background: var(--light);border-top: var(--spacer-1) 
     solid #f6f6f6;}
  .footer__store {color: #003765;font-weight: 600;}
  .footer__title {color: #003765;font-weight: 600;}
  .i-chevron-right{font-size:0;}

}
