css:
/*pc*/@media (min-width: 601px){
  .header{box-shadow: -6px 15px 14px -9px rgba(0,0,0,0.37);}
.header__logo { max-height: 120px; max-width: 300px;}
#search-input {border: 1px solid #ffffff;width: 200px;margin-left: 590px}
.header__search-input {border-radius: 15px;} 
.header__nav {font-size: 16px;margin-top: -64px;width: 70%;margin-left: 170px;}
.header__nav a {color: #003765;border-bottom: 2px solid transparent;} 
 .header__nav a:hover { border-bottom-color: 2px solid #00882e;color:#00882e;}
  .header__category:hover {background:transparent;text-decoration: none;}
  a:hover {color: #003765;text-decoration: none;}
  a {color: #000;}
  .top-bar__page-links a { font-weight: 500;}
  .top-bar__nav a { color: #fff;}
  .top-bar {background: #00882e;width: 100%;}
  h4{color:#003765;}
.product-card__name{font-size: 16px;padding-top: 10px;}
  .prices__value {color:#00882e;}
}
 /*mobile*/@media (max-width: 600px) {
   .top-bar{{display:none;}

}

 /*responsivo*/
   .header__buttons .btn:hover {color: #00882e;font-size:30px;}
   .info-bar__item {font-size: 16px;}
   .header__search-btn{color: #ef7115;}
   .i-user{color:#003765;font-size: 30px;}
   .i-shopping-bag{color:#00882e;font-size: 30px;}
   .cart-count{background-color: #ef7115;border-radius:10px;
    color: var(--primary-yiq);}
   .info-bar {background: #003765;}
  .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
   .btn-primary {background-color: #de192c;border-radius: 3px;font-size: 14px;
     font-weight: 500;border-color: #de192c;}
   .btn-primary a:hover {background-color: #00882e;border-radius: 3px;font-size: 14px;
     font-weight: 500;border-color: #00882e;}
   .footer {background: var(--light);border-top: var(--spacer-1) solid #f6f6f6;
}
   .footer__store {color: #003765;font-weight: 600;}
   .footer__title {color: #003765;font-weight: 600;}
   .i-chevron-right{font-size:0;}

}
