css:
/*pc*/@media (min-width: 601px){
  .header__logo {max-height: 90px;
    max-width: 300px;}
  .header__search-input {
            margin-top: 0px;
        margin-left: 600px;
        border-radius: 0px;
        width: 200px;font-size:0px; border: 0px;
    BACKGROUND: transparent!important;}
  #search-form{  width: 230px;}
  .header__search-btn {
    color: #ff880e;}    
  .header__search-btn:hover {color: #ffffff; }
.i-search{color: #ffffff;}
  .header{background: #003765;
background: -webkit-linear-gradient(193deg,rgba(0, 55, 101, 1) 0%, rgba(25, 109, 179, 1) 50%, rgba(73, 155, 222, 1) 100%);
background: -moz-linear-gradient(193deg,rgba(0, 55, 101, 1) 0%, rgba(25, 109, 179, 1) 50%, rgba(73, 155, 222, 1) 100%);
background: linear-gradient(193deg,rgba(0, 55, 101, 1) 0%, rgba(25, 109, 179, 1) 50%, rgba(73, 155, 222, 1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(
  startColorstr="#003765",
  endColorstr="#499BDE",
  GradientType=0
);box-shadow: -6px 15px 14px -9px rgb(112 112 112 / 37%);}
  .header__nav {width: 700px;
    margin-left: 330px;
        font-size: 15px;
        margin-top: -80px;
    }

  .header__nav a {font-weight: 700;
    border-bottom: 2px solid transparent;
    color: #FFF !important;padding: 20px 8px 20px 10px;
        border-radius: 0px;}
 .header__nav a:hover { border-bottom-color: transparent;}
 .header__nav a:hover { color:#FFF!important;font-weight: 700;}
  .header__nav a:hover { border-bottom-color: #FFF;}
  .header__category:hover {background:transparent;color:#003765!important;
    text-decoration: none;}
  a:hover {color: #003765!important;text-decoration: none;}
  a {color: #000;}
  .top-bar__page-links a { font-size:12px;font-weight: 500;}
  .top-bar__nav a { color: #fff;}
  .top-bar {background: #003765;
background: -webkit-linear-gradient(163deg, rgba(0, 55, 101, 1) 0%, rgba(17, 43, 64, 1) 50%, rgba(75, 115, 156, 1) 100%);
background: -moz-linear-gradient(163deg, rgba(0, 55, 101, 1) 0%, rgba(17, 43, 64, 1) 50%, rgba(75, 115, 156, 1) 100%);
background: linear-gradient(163deg, rgba(0, 55, 101, 1) 0%, rgba(17, 43, 64, 1) 50%, rgba(75, 115, 156, 1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#003765", endColorstr="#4B739C", GradientType=0);width: 100%;}
  h4{color:#003765;}
  h5{font-size: 15px;}
  h1 {font-size: 26px;color:#003765;font-weight: 600;}
  .prices--big .prices__value {font-size: 18px;color:#00882e;}
.product-card__name{font-size: 16px;padding-top: 10px;}
  .lead {font-size: 15px;color: #7a7a7a;}

  .prices--big .prices__value {
    font-size: 23px;
    color: #00882e;
}
}
 /*mobile*/@media (max-width: 600px) {
   .top-bar{display:none;}
a:hover {color: #003765;text-decoration: none;}
  a {color: #000;}
  h4{color:#003765;}
  h5{font-size: 15px;}
  h1 {font-size: 20px;color:#003765;font-weight: 600;}
  .prices--big .prices__value {font-size: 18px;color:#00882e;}
.product-card__name{font-size: 16px;padding-top: 10px;}
  .lead {font-size: 15px;color: #7a7a7a;}

  .prices--big .prices__value {
    font-size: 18px;
    color: #00882e;
}
   }

 /*responsivo*/
.card-header {background-color: #084b85;}
.hero-banner {
  margin: 0;}
.prices--literal small {
    font-size: 15px;color:#212529;}
   .variations__option--selected:not(.disabled) {
    border-color: #00882e;
    box-shadow: 0 0 0 0.1rem rgba(var(--secondary-rgb),.6);}
   .variations__grid-title {font-size:16px;}
   .html-clearfix {border-radius:20px;padding: 15px 30px;border: 1px solid #e9e9e9;}
   .p {font-family: 'Poppins';}
   .badge-primary {background-color: #00882e;}
   .menu__footer a {color: #373b3e;text-decoration: none;}
.menu__footer a:hover {
    color: #003765;text-decoration: none;}
  .btn-primary {
    background-color: #f8f9fa;
    border-color: #003765;
    color: #003765;}
.btn-primary:hover {background-color: #3873a5;
    border-color: #3873a5;
    color: #ffffff;}
  .btn-success {
     background-color: #f8f9fa;
    border-color: #003765;
    color: #003765;}
.btn-outline-secondary a:hover {
    border-color: var(--secondary);
    color: #FFF;
}
.btn-success a:hover {
    background-color: background-color: #3873a5;
    border-color: #3873a5;
    color: #ffffff;}
   .btn-light:hover {background-color: #00882e;border-color:#00882e;
    color: #ffffff;border-radius:20px;}
   .btn-light { border-radius:20px;  background-color: #ebebeb;
    border-color: #dddddd;color: #212529;}
  .btn-light:disabled {border-radius:20px;background-color: #f6f6f6;
   border-color: #f6f6f6;color:#b7b7b7;}
    .close {color:#122b40;font-size: 26px;}
   .prices__value {color: #003765;}
   .glide__arrow {color: #00882e;}
   .header__buttons .btn{color: #003765;font-size:30px;}
   .header__buttons .btn:hover {color: #00882e;font-size:30px;}
   .badge-primary {background-color: #00882e;border-radius: 20px;}
   .header__buttons .btn .badge {font-size: 14px;}
   .info-bar__item {font-size: 16px;}
.info-bar__item a:hover{color:#fff !important;}
   .header__search-btn{color:#003765;}
   .i-user{color:#fff;font-size: 30px;}
   .i-shopping-bag{color:#fff;font-size: 30px;}
   .cart-count{background-color: #ef7115;border-radius:10px;
    color: var(--primary-yiq);}
   .info-bar {background: #ef7115;}
   .i-shipping-fast{font-size:20px;}
   .product__short-stock{display:none;}
  .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
  #collapse-escolha_o_tamanho_cavalo{display:none;}
#collapse-escolha_o_formato{display:none;}
#collapse-escolha_a_cor_{display:none;}
#collapse-escolha_a_quantidade_{display:none;}
#collapse-Brands{display:none !important;}
.search-engine__filter--Brands{display:none !important;}
.search-engine__filter--escolha_o_formato{display:none !important;}
.search-engine__filter--escolha_a_cor_{display:none !important;}
.search-engine__filter--escolha_a_quantidade_{display:none !important;}
.search-engine__filter--escolha_o_tamanho_cavalo{display:none !important;}
  .footer {background: var(--light);border-top: var(--spacer-1) 
     solid #f6f6f6;}
  .footer__store {color: #003765;font-weight: 600;}
  .footer__title {color: #003765;font-weight: 600;}
  .i-chevron-right{font-size:0;}
   .card-header {background-color: #ebebeb;}
.search-engine__option {
    color: #4e4e4e;}
.search-engine__nav {background: #ffffff;}
.menu__item {
    align-items: center;
    border-bottom: var(--border-width) solid #dddddd;
  color: #000000;}
.menu__item:hover {
    background: #022542;
    text-decoration: none;color:#fff !important;}
.menu__item a:hover {
    background: #022542;
    text-decoration: none;color:#fff !important;}
.pb-lg-4{padding-bottom: 0rem;}
.app__footer {
    background: linear-gradient(180deg, var(--light) 0, var(--body-bg) 90%);
    border-top: var(--spacer-1) solid #dee1e5;}
#search-input {border: 1px solid #e4f2ff;}
.header__toggler-icon {
    color: #ffffff;
    font-size: 1.3rem;
    margin-bottom: -.13rem;
    margin-right: var(--spacer-1);
}
}
